INSERT INTO `ps_blog` VALUES ('25', '第二篇比克标题', '1', '1', '\r\nbool BindSystem::ThreadInitialize(IKernel* pKernel)\r\n{\r\n	MODULE_ADD_CALLBACK(\"Player\", \"OnLoad\", BindSystem::OnPlayerLoad);\r\n	MODULE_ADD_CALLBACK(\"Player\", \"OnReady\", BindSystem::OnPlayerReady);\r\n\r\n	MODULE_ADD_CALLBACK(\"BaseItem\", \"OnCreateClass\", BindSystem::OnItemCreateClass);\r\n\r\n	MODULE_ADD_CALLBACK(\"BindBox\", \"OnCreate\", BindSystem::OnBindBoxCreate);\r\n	MODULE_ADD_CALLBACK(\"EquipBox\", \"OnAfterAdd\", BindSystem::OnEquipBoxAfterAdd);\r\n	MODULE_ADD_CALLBACK(\"ToolBox\", \"OnAfterAdd\", BindSystem::OnToolBoxAfterAdd);\r\n\r\n	MODULE_ADD_COMMANDMAP(\"BaseItem\", COMMAND_ITEM_CAN_BECHUCK, BindSystem::OnCommandCanChuckItem);\r\n	MODULE_ADD_COMMANDMAP(\"BaseItem\", COMMAND_ITEM_CAN_BEEXCHANGE, BindSystem::OnCommandCanExchangeItem);\r\n	MODULE_ADD_COMMANDMAP(\"BaseItem\", COMMAND_ITEM_CAN_REBUILD, BindSystem::OnCommandCanRebuildItem);\r\n	\r\n	MODULE_ADD_COMMANDMAP(\"BindBox\", COMMAND_CAN_ADDITEM, BindSystem::OnCommandBindBoxCanAddItem);\r\n\r\n	//MODULE_ADD_CUSTOMMAP(CLIENT_CUSTOMMSG_BIND, BindSystem::OnCustomBind);\r\n	return true;\r\n}\r\n\r\n//释放\r\nbool BindSystem::Destroy()\r\n{\r\n	return true;\r\n}\r\n\r\n//接口\r\n//获得物品的绑定类型\r\nint BindSystem::GetBindType(IKernel * pKernel, const PERSISTID & item)\r\n{\r\n	if (!pKernel->Find(item, \"BindType\"))\r\n		return BIND_TYPE_NONE;\r\n\r\n	return pKernel->QueryInt(item, \"BindType\");\r\n}', null, null, null, '1340782076', '1340782076', '8', '0', '0');
INSERT INTO `ps_blog` VALUES ('26', '谷歌创建智能网络系统：大脑模拟器能识别猫咪', '1', '1', '网易科技讯 6月26日消息，据国外媒体报道，Google X部门的科学家们通过将1.6万台电脑的处理器相连接建造出了全球为数不多的最大中枢网络系统，它能自主学习，可以称之谓“谷歌大脑”。科学家们表示，这一中枢网络系统完全是在网络中自学成才。\r\n\r\n面对从YouTube视频中找到的1000万张数字照片，这个“谷歌大脑”会干什么？这估计与数以百万普通YouTube用户一样——找猫咪。\r\n\r\n科学家们说到，该中枢系统能够靠“自己”识别出一只没有做出任何动作的猫。据悉，科学家们打算于本周将此项结果展示在苏格兰爱丁堡一次的科学会议上。\r\n\r\nGoogle的科学家以及程序员们表示，互联网上充斥着许多猫咪视频算不上什么新闻，不过这种模拟的效果还是令他们吓了一跳。与之前所做的任何实验相比，谷歌大脑的效果都要好得多：面对2万种截然不同的物体，它的辨识能力提高了很多。\r\n\r\n科学家们声称，这项研究是新一代计算机科学的代表，这类研究项目充分利用了计算资源的成本下滑，以及庞大数据中心的日益增多的特点。同时它在机器视觉、认知、话语识别以及语言翻译领域都代表着最为前沿的科学技术。\r\n\r\n事实上，并不仅仅只有Google的科学家们在机器模拟人脑领域探索，有人将其看作是一种更为深层次学习的模式。微软的科学家们去年发现该技术也可用于建造理解人类话语的计算机系统。', null, null, null, '1340782139', '1340782139', '8', '0', '0');
