-- ----------------------------
-- Table structure for `ps_login`
-- ----------------------------
DROP TABLE IF EXISTS `ps_login`;
CREATE TABLE `ps_login` (
  `login_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `login_name` varchar(255) NOT NULL COMMENT '登录名 使用email',
  `login_pass` char(32) NOT NULL COMMENT '登录密码 md5加密',
  `login_salt` char(20) NOT NULL COMMENT 'md5(salt . pass) md5加密盐度',
  `login_online` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否在线',
  `login_last_ip` varchar(16) NOT NULL DEFAULT '' COMMENT '最后登录ip',
  `login_last_time` int(11) NOT NULL DEFAULT '0' COMMENT '最后登录时间',
  `login_count` int(11) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `login_frozen` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否被冻结',
  `login_all_time` int(11) DEFAULT NULL COMMENT '总计在线时间',
  `login_resetpw_time` int(11) DEFAULT NULL COMMENT '重置密码过期时间戳 （1970年1月1日到某天的秒数）',
  `login_resetpw_rand` char(8) DEFAULT NULL COMMENT '重置密码连接的随机字符串',
  PRIMARY KEY (`login_id`),
  UNIQUE KEY `login_name_unique` (`login_name`),
  UNIQUE KEY `login_id_unique` (`login_id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

