CREATE TABLE `ps_relationship` (
  `relationship_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `relationship_user` int(10) unsigned NOT NULL COMMENT '用户',
  `relationship_follower` int(10) unsigned NOT NULL COMMENT '关注者',
  `relationship_mode` enum('1','2','3') NOT NULL DEFAULT '1' COMMENT '1user关注follower  2follower关注user  3互相关注',
