-- ----------------------------
-- Table structure for `ps_user`
-- ----------------------------
DROP TABLE IF EXISTS `ps_user`;
CREATE TABLE `ps_user` (
  `user_id` int(10) unsigned NOT NULL COMMENT '账户id',
  `user_nick` tinytext NOT NULL COMMENT '昵称 初始为登录名@前部分',
  `user_sex` enum('0','1','2') DEFAULT '0' COMMENT '1男 2女 0人妖',
  `user_photo` longblob COMMENT '头像，限制63k以下',
  `user_photo_type` varchar(32) DEFAULT NULL COMMENT 'photo头像文件格式png,jpg,bmp,...',
  `user_blog_name` tinytext COMMENT '博客名称 默认为登录名@前部分',
  `user_blog_info` tinytext COMMENT '博客介绍',
  `user_domain` varchar(128) DEFAULT NULL COMMENT '个性域名 默认为登录名@前部分加上网站url',
  `user_followers` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关注者数量',
  `user_befollowers` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '被关注者',
  `user_blogs` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发表博客数',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `user_id_unique` (`user_id`) USING BTREE,
  CONSTRAINT `user_id_forei` FOREIGN KEY (`user_id`) REFERENCES `ps_login` (`login_id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

